import React from 'react'
import './bootstrap.min.css'

class MainPage extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			games: []
		}
	}
	componentDidMount() {
		this.updateGames()
	}
	async updateGames() {
		let res = await fetch("/api/getGames", {
			method: "POST",
			body: null,
			headers: {}
		})
		let games = await res.json()
		this.state.games = []
		games.map((e, i) => {
			this.state.games.push(<Game name={e.name} players={e.players} number={i + 1} status={e.status} />)
		})
		this.forceUpdate()
	}
	render() {
		return (
			<div className="container">
				<table className="table table-dark table-striped">
					<thead>
				    	<tr>
				      		<th scope="col">#</th>
				      		<th scope="col">Name</th>
				      		<th scope="col">Players</th>
				      		<th scope="col">Status</th>
				    	</tr>
				  	</thead>
				  	<tbody>
				 		{this.state.games}
				  	</tbody>
				</table>
				<div className="form-row text-center">
	    			<button className="btn btn-outline-success">+Create New Game</button>
	 			</div>
 			</div>
		)
	}
}

class Game extends React.Component {
	constructor(props) {
		super(props)
	}
	render() {
		return (
			<tr style={{ cursor: 'pointer' }}>
			   	<th scope="row">{this.props.number}</th>
			    <td>{this.props.name}</td>
			    <td>{this.props.players + "/2"}</td>
			    <td>{this.props.status}</td>
			</tr>
		)
	}
}

export default MainPage
