import React from 'react'
import openSocket from 'socket.io-client';
import './GamePage.css'
import './bootstrap.min.css'

const socket = openSocket('http://localhost:3000')


class GamePage extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			state: ""
		}
	}
	componentDidMount() {
		socket.on("redirectOnMain", () => window.location.href = "/")
		socket.on("updateClient", (game) => {
			this.setState({ state: game.state })
		})
	}
	render() {
		return (
			<GameBoard state={this.state} />
		)
	}
}

class GameBoard extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			state: this.props.state,
			canvas: null
		}
	}
	componentDidMount() {
		let canvas = document.getElementById("canvas")
		canvas.width = canvas.parentNode.offsetWidth
		canvas.height = canvas.width
		this.state.canvas = canvas
		this.state.ctx = canvas.getContext("2d")
		this.drawBoard()
	}
	shouldComponentUpdate(props) {
		this.setState({ state: props.state })
	}
	drawBoard() {
		const width = this.state.canvas.width
		const height = this.state.canvas.height
		this.state.ctx.fillStyle = "black"
		this.state.ctx.fillRect(width / 3, 0, 2, height)
		this.state.ctx.fillRect(2 * width / 3, 0, 2, height)
	}
	drawState() {
		this.drawBoard()
	}
	render() {
		return (
			<div className="container text-center">
				<canvas id="canvas"></canvas>
			</div>
		)
	}
}

export default GamePage
