import React from 'react'
import openSocket from 'socket.io-client';
import './GamePage.css'
import './bootstrap.min.css'

const socket = openSocket('http://localhost:3000')

class GamePage extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			state: ""
		}
	}
	componentDidMount() {
		socket.on("redirectOnMain", () => window.location.href = "/")
		socket.on("updateClient", (game) => {
			this.setState({ state: game.state })
		})
	}
	render() {
		return (
			<GameBoard state={this.state} />
		)
	}
}

class GameBoard extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			state: this.props.state,
			height: 0,
			width: 0
		}
	}
	componentDidMount() {
		window.onresize = () => {
			this.setState({ width: document.container.offsetWidth, height: document.container.offsetHeight })
		}
	}
	shouldComponentUpdate(props) {
		this.setState({ state: props.state })
	}
	render() {
		return (
			<div className="container" id="container">
				<canvas id="canvas" width={this.state.width} height={this.state.height}></canvas>
			</div>
		)
	}
}

export default GamePage
