import React from 'react'
import './bootstrap.min.css'

class MainPage extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			games: [],
			modal: "modal fade"
		}
	}
	componentDidMount() {
		this.updateGames()
	}
	async updateGames() {
		let res = await fetch("/api/getGames", {
			method: "POST",
			body: null,
			headers: {}
		})
		let games = await res.json()
		this.state.games = []
		games.map((e, i) => {
			this.state.games.push(<Game name={e.name} players={e.players} number={i + 1} status={e.status} />)
		})
		this.forceUpdate()
	}
	showModal() {
		this.setState({ modal: "modal fade show" })
	}
	render() {
		return (
			<div className="container">
				<table className="table table-dark table-striped">
					<thead>
				    	<tr>
				      		<th scope="col">#</th>
				      		<th scope="col">Name</th>
				      		<th scope="col">Players</th>
				      		<th scope="col">Status</th>
				    	</tr>
				  	</thead>
				  	<tbody>
				 		{this.state.games}
				  	</tbody>
				</table>
				<div className="form-row text-center">
	    			<button className="btn btn-outline-success" onClick={this.showModal.bind(this)}>+Create New Game</button>
	 			</div>
	 			<Modal fade={this.state.modal} />
 			</div>
		)
	}
}

class Game extends React.Component {
	constructor(props) {
		super(props)
	}
	render() {
		return (
			<tr style={{ cursor: 'pointer' }}>
			   	<th scope="row">{this.props.number}</th>
			    <td>{this.props.name}</td>
			    <td>{this.props.players + "/2"}</td>
			    <td>{this.props.status}</td>
			</tr>
		)
	}
}

class Modal extends React.Component {
	constructor(props) {
		super(props)
	}
	render() {
		return (
			<div className={this.props.fade} tabindex="-1"> 
  				<div className="modal-dialog">
    				<div className="modal-content">
      					<div className="modal-header">
        					<h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
        					<button className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      					</div>
      					<div className="modal-body">
        					...
      					</div>
      					<div className="modal-footer">
        					<button className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        					<button className="btn btn-primary">Save changes</button>
      					</div>
    				</div>
  				</div>
			</div>
		)
	}
}

export default MainPage
